import React, {useState} from 'react';

const ComicThumbnail = ({ id='', url='', title='', thumbnail='', description='', writer='', penciller='', published='' }) => (
    <React.Fragment>
         <div id={id} className="thumbnail wrapper" onClick={(e) => {

    e.preventDefault();

   console.log(e.target.name);



 alert('\nTitle : ' + title + '\n\nDescription' + description + 
          '\n\nWriter : ' + writer + '\n\nPenciller : ' + penciller + '\n\nPublished : ' + published);
}}>

     <div id="modal-insert-coords" class="md-modal">
            <div class="md-modal-content md-card-4 md-animate-zoom" style="max-width:600px">
                <h1>Informe as coordenadas iniciais do mapa</h1>
                <p></p>
                    <div>
                        <button onclick="document.getElementById('modal-insert-coords').style.display = 'none'" type="button" class="md-button md-red md-margin-right">Cancel</button>
                        <button onclick="initMap(); document.getElementById('modal-insert-coords').style.display = 'none'" type="button" class="md-button md-dark-grey">Confirmar</button>
                    </div>
            </div>
        </div>
            <img className="thumbnail-img" src={thumbnail} alt={thumbnail}/>
            <div className="thumbnail-overlay"><span className="thumbnail-text">{title}</span>
            </div>
        </div>
    </React.Fragment>


);

export default ComicThumbnail;


export const itemSelected = (status, id) => {

    return status;
};
